import { describe, it, expect } from "vitest";
import { longestPalindrome } from "./longest_palindromic_substring";

describe("longest palindromic substring tests", function () {
  it("should pass the first test", function () {
    const expectedOutput = "bab";
    const output = longestPalindrome("bab");
    expect(output).toEqual(expectedOutput);
  });

  it("should pass the second test", function () {
    const expectedOutput = "bb";
    const output = longestPalindrome("cbbd");
    expect(output).toEqual(expectedOutput);
  });

  it("should pass the third test", function () {
    const expectedOutput = "fklkf";
    const output = longestPalindrome(
      "",
    );
    expect(output).toEqual(expectedOutput);
  });
});
